                $('body').delegate('.decrease', 'click', function (e) {
                    if (y > 0) {
                        $('*').each(function () {
                            var x = parseInt($(this).css("font-size"));
                            x = x - 1;
                            $(this).css("font-size", x);
                        });
                        y = y - 1;
