            if (isHindi && !currentUrl.includes('/hi/')) {
                console.log(langLink.innerText);
                console.log("hindi");
                console.log(newUrl);
                newUrl = baseUrl + '/hi' + window.location.pathname + window.location.search;
            }
            else if (!isHindi && currentUrl.includes('/hi/')) {
                console.log("en");
                newUrl = baseUrl + window.location.pathname.replace('/hi', '') + window.location.search;


            } else {
                console.log("else");

                return;
            }

            console.log(newUrl);

            window.history.pushState({}, '', newUrl);
