        function tabControl() {
            var tabsss = jQuery('.tabbed-contentss').find('.tabsss');
            if (tabsss.is(':visible')) {
                tabsss.find('a').on('click', function (event) {
                    event.preventDefault();
                    var target = $(this).attr('href'),
                        tabsss = $(this).parents('.tabsss'),
                        buttons = tabsss.find('a'),
                        itemss = tabsss.parents('.tabbed-contentss').find('.itemss');
                    buttons.removeClass('active');
                    itemss.removeClass('active');
                    $(this).addClass('active');
                    $(target).addClass('active');
                });
            } else {
                $('.itemss').on('click', function () {
                    var container = $(this).parents('.tabbed-contentss'),
                        currId = $(this).attr('id'),
                        itemsss = container.find('.itemss');
                    container.find('.tabsss a').removeClass('active');
                    itemsss.removeClass('active');
                    $(this).addClass('active');
                    container.find('.tabsss a[href$="#' + currId + '"]').addClass('active');
                });
            }
        }

        $(document).ready(function () {
            // Main navigation dropdown handling
            $('.drodown-link, .internet-banking a,.right-anchor').click(function (e) {
                e.stopPropagation(); // Prevent the click event from bubbling up to the document
                var $parentLi = $(this).parents('.multi-dropdown, .internet-banking, .r-dropdown');

                if ($parentLi.hasClass('focusactive')) {
                    // Close the menu if it's already active
                    $parentLi.removeClass('focusactive');
                } else {
                    // Open the menu and close all other submenus
                    $('.multi-dropdown,.r-dropdown').removeClass('focusactive');
                    $parentLi.addClass('focusactive');
                }
            });

            // Submenu handling for nested dropdowns
            $('.dropdown-link2').click(function (f) {
                f.stopPropagation(); // Prevent the click event from bubbling up to the document
                var $parentLi = $(this).parents('.sub-menu li');

                if ($parentLi.hasClass('active-sub-menu')) {
                    // Close the submenu if it's already active
                    $parentLi.removeClass('active-sub-menu');
                } else {
                    // Open the submenu and close all other nested submenus
                    $('.sub-menu li').removeClass('active-sub-menu');
                    $parentLi.addClass('active-sub-menu');
                }
            });

            // Escape key handling to close submenus
            $(document).on('keydown', function (event) {
                if (event.key === "Escape") {
                    $('.multi-dropdown, .internet-banking, .r-dropdown').removeClass('focusactive');
                    $('.main-navigation ul.sub-menu li').removeClass('active-sub-menu');
                }
            });

            // Close dropdowns when clicking outside of the menu or submenus
            $(document).on('click', function (event) {
                // Define the elements to check clicks against
                var $trigger = $(".multi-dropdown, .mob-nav");  // Main navigation and mobile navigation

                // Check if the click was outside of the defined elements
                if (!$(event.target).closest($trigger).length) {
                    // Only remove 'focusactive' class from multi-dropdown and internet-banking
                    $('.multi-dropdown, .internet-banking').removeClass('focusactive');

                    // Remove 'active-sub-menu' class from any open submenus
                    $('.main-navigation ul.sub-menu li').removeClass('active-sub-menu');
                }
            });


        });




