         $(document).ready(function () {
  var localstor_name = localStorage.getItem("bodybg");
  console.log(localstor_name);

  // Restore theme state
  if (localstor_name === 'black add') {
    $('body').addClass('colorbalck');
    $('.colorbalckbtn')
      .removeClass('colorbalckbtn')
      .addClass('colordefault')
      .find('a')
      .attr({
        'aria-label': 'Deactivate color blind mode',
        'title': 'Deactivate color blind mode'
      });
  }

  // Activate color-blind mode
  $("body").on("click", ".colorbalckbtn", function (e) {
    e.preventDefault();
    $(this)
      .removeClass('colorbalckbtn')
      .addClass('colordefault')
      .find('a')
      .attr({
        'aria-label': 'Deactivate color blind mode',
        'title': 'Deactivate color blind mode'
      });

    $('body').addClass('colorbalck');
    localStorage.setItem("bodybg", "black add");
  });

  // Deactivate color-blind mode
  $("body").on("click", ".colordefault", function (e) {
    e.preventDefault();
    $(this)
      .removeClass('colordefault')
      .addClass('colorbalckbtn')
      .find('a')
      .attr({
        'aria-label': 'Activate color blind mode',
        'title': 'Activate color blind mode'
      });

    $('body').removeClass('colorbalck');
    localStorage.setItem("bodybg", "black remove");
  });
});

